mapper.select:
SELECT COUNT(id) as num_int1,DATE_FORMAT(ctime,'%Y-%m-%d') as date_str
FROM hl_user u

		
			
			and ctime >= #{startTime}
			
		
		
			
			and ctime < #{endTime}
			
		

group by DATE_FORMAT(ctime,'%Y-%m-%d')
order by DATE_FORMAT(ctime,'%Y-%m-%d') asc

SELECT COUNT(id) as num_int1, DATE_FORMAT(ctime,'%Y-%m') as date_str
FROM hl_user u

		
			
			and ctime >= #{startTime}
			
		
		
			
			and ctime < #{endTime}
			
		

group by DATE_FORMAT(ctime,'%Y-%m')
order by DATE_FORMAT(ctime,'%Y-%m') asc

SELECT i.date_str, SUM(i.num_int1) as num_int1, SUM(i.num_int2) as num_int2, SUM(i.num_int3) as num_int3 FROM( SELECT DATE_FORMAT(ctime,'%Y-%m-%d') as date_str,COUNT(id) as num_int1,0 as num_int2,0 as num_int3 FROM hl_express_find
AND state=1
and ctime >= #{startTime}
and ctime < #{endTime}
group by DATE_FORMAT(ctime,'%Y-%m-%d') union all SELECT DATE_FORMAT(ctime,'%Y-%m-%d') as date_str,0 as num_int1,COUNT(id) AS num_int2,0 as num_int3 FROM hl_express_find
AND state=0
and ctime >= #{startTime}
and ctime < #{endTime}
group by DATE_FORMAT(ctime,'%Y-%m-%d') UNION ALL SELECT DATE_FORMAT(ctime,'%Y-%m-%d') as date_str,0 as num_int1,0 as num_int2,COUNT(*) as num_int3 FROM hl_express_find
and ctime >= #{startTime}
and ctime < #{endTime}
group by DATE_FORMAT(ctime,'%Y-%m-%d') )i group by i.date_str order by i.date_str asc

SELECT i.date_str, SUM(i.num_int1) as num_int1, SUM(i.num_int2) as num_int2, SUM(i.num_int3) as num_int3 FROM( SELECT DATE_FORMAT(ctime,'%Y-%m') as date_str,COUNT(id) as num_int1,0 as num_int2,0 as num_int3 FROM hl_express_find
and state =1
and ctime >= #{startTime}
and ctime < #{endTime}
GROUP BY DATE_FORMAT(ctime,'%Y-%m') UNION ALL SELECT DATE_FORMAT(ctime,'%Y-%m') as date_str,0 as num_int1,COUNT(id) AS num_int2,0 as num_int3 FROM hl_express_find
AND state =0
and ctime >= #{startTime}
and ctime < #{endTime}
GROUP BY DATE_FORMAT(ctime,'%Y-%m') UNION ALL SELECT DATE_FORMAT(ctime,'%Y-%m') as date_str,0 as num_int1,0 as num_int2,COUNT(*) as num_int3 FROM hl_express_find
and ctime >= #{startTime}
and ctime < #{endTime}
GROUP BY DATE_FORMAT(ctime,'%Y-%m') )i group by i.date_str order by i.date_str asc

SELECT SUM(sc.weight1+sc.weight2+sc.weight3+sc.weight4+sc.weight5
+sc.weight6+sc.weight7+sc.weight8+sc.weight9) AS num_int1,DATE_FORMAT(flush_time,'%Y-%m-%d') AS date_str
FROM hl_service_cust_child sc

		
			
			and flush_time >= #{startTime}
			
		
		
			
			and flush_time < #{endTime}
			
		

group by DATE_FORMAT(flush_time,'%Y-%m-%d')

SELECT SUM(sc.weight1+sc.weight2+sc.weight3+sc.weight4+sc.weight5
+sc.weight6+sc.weight7+sc.weight8+sc.weight9) AS num_int1,DATE_FORMAT(flush_time,'%Y-%m') AS date_str
FROM hl_service_cust_child sc

		
			
			and flush_time >= #{startTime}
			
		
		
			
			and flush_time < #{endTime}
			
		

group by DATE_FORMAT(flush_time,'%Y-%m')

SELECT COUNT(e.house_id) AS num_int1 ,DATE_FORMAT(ctime,'%Y-%m-%d') AS date_str FROM hl_express_find e
e.state !=0
and ctime >= #{startTime}
and ctime < #{endTime}
group by DATE_FORMAT(ctime,'%Y-%m-%d')

SELECT COUNT(e.house_id) AS num_int1 ,DATE_FORMAT(ctime,'%Y-%m') AS date_str FROM hl_express_find e
e.state !=0
and ctime >= #{startTime}
and ctime < #{endTime}
group by DATE_FORMAT(ctime,'%Y-%m')

SELECT SUM(sc.weight1+sc.weight2+sc.weight3+sc.weight4
+sc.weight5+sc.weight6+sc.weight7+sc.weight8+sc.weight9) AS num_int1,sc.nickname
FROM hl_service_cust_child sc

		
			
			and flush_time >= #{startTime}
			
		
		
			
			and flush_time < #{endTime}
			
		

group by sc.sc_id

SELECT COUNT(e.house_id) AS num_int1,sc.nickname FROM ( SELECT ee.house_id FROM hl_express_find ee
ee.house_id is not null and ee.state !=0
and ee.ctime >= #{startTime}
and ee.ctime < #{endTime}
) e LEFT JOIN hl_service_cust sc ON e.house_id=sc.sc_id GROUP BY sc.sc_id
